mapper.insert:
/* SQL_ID : go.gg.cms.core.commonMenu.insertMaster */

	INSERT INTO tb_menu (
		id
		, disp_cd
		, parent_id
		, disp_seq
		, link_url
	  	, link_type
	  	, icon_type
		, use_yn
		, del_yn
		, reg_id
		, reg_ip
		, reg_date
		, reg_agent
	) VALUES (
		#{id}::uuid
		, #{cd}
		, #{parentId}::uuid
		, #{seq, jdbcType=INTEGER}
		, #{linkUrl}
		, #{linkType}
		, #{iconType}
		, #{useYn}
		, 'N'
		, #{regId}::uuid
		, #{regIp}
		, now()
		, #{regAgent}
	)

/* SQL_ID : go.gg.cms.core.commonMenu.insertMl */

	INSERT INTO tb_menu_ml (
		id
		, parent_id
		, lang_cd
		, name
		, short_desc
		, long_desc
	) VALUES (
		uuid_generate_v4()
		, #{id}::uuid
		, 'ko'
		, #{name}
		, #{shrtDesc}
		, #{longDesc}
	)

mapper.update:
/* SQL_ID : go.gg.cms.core.commonMenu.updateMaster */

	UPDATE tb_menu SET
		 link_url = #{linkUrl}
	  	, link_type = #{linkType}
	  	, icon_type = #{iconType}
		, use_yn = #{useYn}
		, mod_id = #{modId}::uuid
		, mod_ip = #{modIp}
		, mod_date = now()
		, mod_agent = #{modAgent}
	WHERE id = #{id}::uuid

/* SQL_ID : go.gg.cms.core.commonMenu.delete */

	UPDATE tb_menu SET
		del_yn = 'Y'
		, mod_id = #{modId}::uuid
		, mod_ip = #{modIp}
		, mod_date = now()
		, mod_agent = #{modAgent}
	WHERE id = #{id}::uuid

/* SQL_ID : go.gg.cms.core.commonMenu.updateSeq */


	UPDATE tb_menu SET
	  parent_id = temp_tb_menu.parent_id
	, disp_seq = temp_tb_menu.seq
	, mod_date = now()
	, mod_id = #{modId}::uuid
	, mod_ip = #{modIp}
	, mod_agent = #{modAgent}
	FROM (


	SELECT #{menu.id}::uuid AS id, #{menu.parentId}::uuid AS parent_id, #{menu.seq, jdbcType=INTEGER} AS seq


	) temp_tb_menu
	WHERE tb_menu.id = temp_tb_menu.id
