mapper.select:
/* SQL_ID : go.gg.cms.apps.sample.findList */
SELECT sample.id AS id , sample.category_cd AS category_cd , sample.file_id AS file_id , sample.disp_seq AS disp_seq , sample.reg_id AS reg_id , sample.reg_ip AS reg_ip , sample.reg_date AS reg_date , sample.reg_agent AS reg_agent , sample.mod_id AS mod_id , sample.mod_ip AS mod_ip , sample.mod_date AS mod_date , sample.mod_agent AS mod_agent , sample.test_date AS test_date , sample_ml.name AS name , sample_ml.short_desc AS short_desc , sample_ml.long_desc AS long_desc FROM tb_sample sample INNER JOIN tb_sample_ml sample_ml ON sample.id = sample_ml.parent_id WHERE sample.del_yn = 'N'
, sample.disp_seq ASC OFFSET (#{pageIndex} - 1) * #{pageUnit} LIMIT #{pageUnit}

mapper.insert:
/* SQL_ID : go.gg.cms.apps.sample.insertMl */

INSERT INTO tb_sample_ml (
	id
	, parent_id
	, lang_cd
	, name
	, short_desc
	, long_desc
) VALUES (
	uuid_generate_v4()
	, #{id}::uuid
	, 'ko'
	, #{name}
	, #{shortDesc}
	, #{longDesc}
)

mapper.update:
/* SQL_ID : go.gg.cms.apps.sample.updateMl */

UPDATE tb_sample_ml SET
	name = #{name}
	, short_desc = #{shortDesc}
	, long_desc = #{longDesc}
WHERE
	parent_id = #{id}::uuid
